import { Link } from "react-router-dom";

export default function ProductCard({ product, addToCart }) {
  return (
    <div className="bg-white rounded-xl shadow-card hover:shadow-xl transition overflow-hidden">
      
      {/* IMAGE */}
      <div className="relative h-52 overflow-hidden">
        <img
          src={product.image}
          alt={product.title}
          className="w-full h-full object-cover hover:scale-110 transition duration-300"
        />
      </div>

      {/* CONTENT */}
      <div className="p-4 text-center">
        <h4 className="font-semibold text-gray-800">
          {product.title}
        </h4>

        <p className="text-sm text-gray-500 mt-1">
          ⭐ {product.rating} rating
        </p>

        <p className="text-pink-600 font-bold mt-2">
          ₹{product.price}
        </p>

        <div className="flex justify-center gap-2 mt-4">
          {addToCart && (
            <button
              onClick={() => addToCart(product)}
              className="px-3 py-1.5 text-sm bg-green-600 text-white rounded hover:bg-green-700 transition"
            >
              Add to Cart
            </button>
          )}

          <Link
            to={`/product/${product.id}`}
            className="px-3 py-1.5 text-sm border border-pink-600 text-pink-600 rounded hover:bg-pink-600 hover:text-white transition"
          >
            View
          </Link>
        </div>
      </div>
    </div>
  );
}
