import { Link } from "react-router-dom";

export default function ProductCard({ product, addToCart }) {
  return (
    <div className="bg-white rounded-lg shadow-md hover:shadow-xl transition overflow-hidden">
      
      {/* IMAGE FIX */}
      <div className="h-48 w-full overflow-hidden">
        <img
          src={product.image}
          alt={product.title}
          className="h-full w-full object-cover"
        />
      </div>

      <div className="p-4 text-center">
        <h3 className="font-semibold text-lg truncate">
          {product.title}
        </h3>

        <p className="text-sm text-gray-500 mt-1">
          ⭐ {product.rating}
        </p>

        <p className="text-pink-600 font-bold mt-2">
          ₹{product.price}
        </p>

        <div className="flex gap-2 mt-4">
          <button
            onClick={() => addToCart(product)}
            className="flex-1 bg-pink-600 text-white py-2 rounded hover:bg-pink-700"
          >
            Add
          </button>

          <Link
            to={`/product/${product.id}`}
            className="flex-1 border border-pink-600 text-pink-600 py-2 rounded"
          >
            View
          </Link>
        </div>
      </div>
    </div>
  );
}
