import { Link } from "react-router-dom";

export default function ProductCard({ product, addToCart }) {
  return (
    <div className="bg-white rounded-xl shadow-card hover:shadow-xl transition">
      <img
        src={product.image}
        alt={product.title}
        className="h-52 w-full object-cover rounded-t-xl"
      />

      <div className="p-4 text-center">
        <h4 className="font-semibold">{product.title}</h4>
        <p className="text-sm text-gray-500">⭐ {product.rating}</p>
        <p className="text-pink-600 font-bold mt-1">₹{product.price}</p>

        <div className="flex justify-center gap-2 mt-3">
          <button
            onClick={() => addToCart(product)}
            className="px-3 py-1 text-sm bg-green-600 text-white rounded"
          >
            Add to Cart
          </button>

          <Link
            to={`/product/${product.id}`}
            className="px-3 py-1 text-sm border border-pink-600 text-pink-600 rounded"
          >
            View
          </Link>
        </div>
      </div>
    </div>
  );
}
