import React from "react";
import { Link, useNavigate } from "react-router-dom";

function Cart({ cartItems, setCartItems }) {
  const navigate = useNavigate();

  const updateQty = (id, type) => {
    setCartItems((prev) =>
      prev.map((item) =>
        item.id === id
          ? {
              ...item,
              qty:
                type === "inc"
                  ? item.qty + 1
                  : Math.max(1, item.qty - 1),
            }
          : item
      )
    );
  };

  const removeItem = (id) => {
    setCartItems((prev) => prev.filter((item) => item.id !== id));
  };

  const total = cartItems.reduce(
    (sum, item) => sum + item.price * item.qty,
    0
  );

  return (
    <div className="container my-5">
      <h3 className="fw-bold mb-4">ðŸ›’ Shopping Cart</h3>

      {cartItems.length === 0 ? (
        <div className="text-center py-5">
          <h5>Your cart is empty</h5>
          <Link to="/categories" className="btn btn-danger mt-3">
            Continue Shopping
          </Link>
        </div>
      ) : (
        <div className="row">
          {/* LEFT â€“ CART ITEMS */}
          <div className="col-md-8">
            {cartItems.map((item) => (
              <div
                key={item.id}
                className="card mb-3 border-0 shadow-sm"
              >
                <div className="card-body">
                  <div className="row align-items-center">
                    
                    {/* IMAGE */}
                    <div className="col-md-2">
                      <img
                        src={item.image}
                        alt={item.title}
                        className="img-fluid rounded"
                        style={{
                          height: "80px",
                          width: "80px",
                          objectFit: "cover",
                        }}
                      />
                    </div>

                    {/* DETAILS */}
                    <div className="col-md-4">
                      <small className="text-muted">Gift Item</small>
                      <h6 className="fw-semibold mb-1">
                        {item.title}
                      </h6>
                      <small className="text-muted">
                        â‚¹{item.price}
                      </small>
                    </div>

                    {/* QTY */}
                    <div className="col-md-3">
                      <div className="d-flex align-items-center gap-2">
                        <button
                          className="btn btn-outline-secondary btn-sm"
                          onClick={() => updateQty(item.id, "dec")}
                        >
                          âˆ’
                        </button>

                        <span className="fw-bold">{item.qty}</span>

                        <button
                          className="btn btn-outline-secondary btn-sm"
                          onClick={() => updateQty(item.id, "inc")}
                        >
                          +
                        </button>
                      </div>
                    </div>

                    {/* PRICE + REMOVE */}
                    <div className="col-md-3 text-end">
                      <h6 className="fw-bold mb-2">
                        â‚¹{item.price * item.qty}
                      </h6>
                      <button
                        className="btn btn-link text-danger p-0"
                        onClick={() => removeItem(item.id)}
                      >
                        Remove
                      </button>
                    </div>

                  </div>
                </div>
              </div>
            ))}
          </div>

          {/* RIGHT â€“ SUMMARY */}
          <div className="col-md-4">
            <div className="card border-0 shadow-sm sticky-top" style={{ top: "100px" }}>
              <div className="card-body">
                <h5 className="fw-bold mb-3">Order Summary</h5>

                <div className="d-flex justify-content-between mb-2">
                  <span>Items</span>
                  <span>{cartItems.length}</span>
                </div>

                <div className="d-flex justify-content-between mb-2">
                  <span>Delivery</span>
                  <span className="text-success">Free</span>
                </div>

                <hr />

                <div className="d-flex justify-content-between fw-bold fs-5">
                  <span>Total</span>
                  <span className="text-danger">â‚¹{total}</span>
                </div>

                <button
                  className="btn btn-danger w-100 mt-4"
                  onClick={() => navigate("/checkout")}
                >
                  Proceed to Checkout
                </button>

                <Link
                  to="/categories"
                  className="btn btn-outline-secondary w-100 mt-2"
                >
                  Continue Shopping
                </Link>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default Cart;
