import { useParams } from "react-router-dom";
import products from "../data/products";

export default function ProductDetails({ addToCart }) {
  const { id } = useParams();
  const product = products.find((p) => p.id === Number(id));

  if (!product) {
    return (
      <h2 className="text-center mt-20 text-xl font-semibold">
        Product not found
      </h2>
    );
  }

  return (
    <div className="max-w-6xl mx-auto px-6 py-12 grid md:grid-cols-2 gap-10">
      <img
        src={product.image}
        alt={product.title}
        className="w-full h-[420px] object-cover rounded-xl shadow-lg"
      />

      <div>
        <h1 className="text-3xl font-bold mb-4">{product.title}</h1>
        <p className="text-gray-600 mb-3">⭐ {product.rating} rating</p>
        <p className="text-2xl font-bold text-pink-600 mb-6">
          ₹{product.price}
        </p>

        <button
          onClick={() => addToCart(product)}
          className="bg-green-600 text-white px-6 py-3 rounded-lg hover:bg-green-700 transition"
        >
          Add to Cart
        </button>
      </div>
    </div>
  );
}
