export default function Checkout({ cartItems }) {
  const total = cartItems.reduce(
    (s, i) => s + i.price * i.qty,
    0
  );

  return (
    <div className="max-w-6xl mx-auto px-6 py-10 grid md:grid-cols-2 gap-8">
      <div>
        <h3 className="font-bold mb-4">Billing Details</h3>
        <input className="w-full mb-3 p-2 border rounded" placeholder="Name" />
        <input className="w-full mb-3 p-2 border rounded" placeholder="Address" />
        <input className="w-full mb-3 p-2 border rounded" placeholder="Phone" />
      </div>

      <div className="bg-gray-100 p-6 rounded">
        <h3 className="font-bold mb-4">Order Summary</h3>
        <p>Total Items: {cartItems.length}</p>
        <p className="font-bold mt-2">Total: â‚¹{total}</p>
        <button className="mt-4 bg-green-600 text-white px-4 py-2 rounded">
          Place Order
        </button>
      </div>
    </div>
  );
}
