import React, { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { getCart, saveCart } from "../utils/cart";

function Cart() {
  const navigate = useNavigate();
  const [cartItems, setCartItems] = useState([]);

  useEffect(() => {
    setCartItems(getCart());
  }, []);

  const updateQty = (id, type) => {
    const updated = cartItems.map((item) =>
      item.id === id
        ? {
            ...item,
            qty:
              type === "inc"
                ? item.qty + 1
                : Math.max(1, item.qty - 1),
          }
        : item
    );

    setCartItems(updated);
    saveCart(updated);
  };

  const removeItem = (id) => {
    const updated = cartItems.filter((item) => item.id !== id);
    setCartItems(updated);
    saveCart(updated);
  };

  const total = cartItems.reduce(
    (sum, item) => sum + item.price * item.qty,
    0
  );

  return (
    <div className="container my-5">
      <h3 className="fw-bold mb-4">ðŸ›’ Shopping Cart</h3>

      {cartItems.length === 0 ? (
        <div className="text-center py-5">
          <h5>Your cart is empty</h5>
          <Link to="/categories" className="btn btn-danger mt-3">
            Continue Shopping
          </Link>
        </div>
      ) : (
        <div className="row">
          {/* LEFT */}
          <div className="col-md-8">
            {cartItems.map((item) => (
              <div key={item.id} className="card mb-3 border-0 shadow-sm">
                <div className="card-body">
                  <div className="row align-items-center">

                    <div className="col-md-2">
                      <img
                        src={item.image}
                        alt={item.title}
                        className="img-fluid rounded"
                        style={{ height: "80px", width: "80px", objectFit: "cover" }}
                      />
                    </div>

                    <div className="col-md-4">
                      <small className="text-muted">Gift Item</small>
                      <h6 className="fw-semibold">{item.title}</h6>
                      <small className="text-muted">â‚¹{item.price}</small>
                    </div>

                    <div className="col-md-3">
                      <div className="d-flex gap-2">
                        <button
                          className="btn btn-outline-secondary btn-sm"
                          onClick={() => updateQty(item.id, "dec")}
                        >
                          âˆ’
                        </button>
                        <span className="fw-bold">{item.qty}</span>
                        <button
                          className="btn btn-outline-secondary btn-sm"
                          onClick={() => updateQty(item.id, "inc")}
                        >
                          +
                        </button>
                      </div>
                    </div>

                    <div className="col-md-3 text-end">
                      <h6 className="fw-bold">â‚¹{item.price * item.qty}</h6>
                      <button
                        className="btn btn-link text-danger p-0"
                        onClick={() => removeItem(item.id)}
                      >
                        Remove
                      </button>
                    </div>

                  </div>
                </div>
              </div>
            ))}
          </div>

          {/* RIGHT */}
          <div className="col-md-4">
            <div className="card border-0 shadow-sm sticky-top" style={{ top: "100px" }}>
              <div className="card-body">
                <h5 className="fw-bold mb-3">Order Summary</h5>

                <div className="d-flex justify-content-between">
                  <span>Items</span>
                  <span>{cartItems.length}</span>
                </div>

                <div className="d-flex justify-content-between">
                  <span>Delivery</span>
                  <span className="text-success">Free</span>
                </div>

                <hr />

                <div className="d-flex justify-content-between fw-bold fs-5">
                  <span>Total</span>
                  <span className="text-danger">â‚¹{total}</span>
                </div>

                <button
                  className="btn btn-danger w-100 mt-4"
                  onClick={() => navigate("/checkout")}
                >
                  Proceed to Checkout
                </button>

                <Link to="/categories" className="btn btn-outline-secondary w-100 mt-2">
                  Continue Shopping
                </Link>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default Cart;
