export default function Checkout({ cartItems }) {
  const total = cartItems.reduce(
    (s, i) => s + i.price * i.qty,
    0
  );

  return (
    <div className="max-w-6xl mx-auto px-6 py-10 grid md:grid-cols-2 gap-10">
      {/* BILLING */}
      <div>
        <h3 className="text-2xl font-bold mb-4">Billing Details</h3>

        <input className="w-full mb-3 p-3 border rounded" placeholder="Full Name" />
        <input className="w-full mb-3 p-3 border rounded" placeholder="Email" />
        <input className="w-full mb-3 p-3 border rounded" placeholder="Phone" />
        <textarea className="w-full mb-3 p-3 border rounded" rows="4" placeholder="Address" />

        <button className="w-full bg-pink-600 text-white py-3 rounded-lg">
          Place Order
        </button>
      </div>

      {/* SUMMARY */}
      <div className="bg-gray-50 p-6 rounded-lg">
        <h3 className="text-2xl font-bold mb-4">Order Summary</h3>

        {cartItems.map((i) => (
          <div key={i.id} className="flex justify-between mb-2">
            <span>{i.title} × {i.qty}</span>
            <span>₹{i.price * i.qty}</span>
          </div>
        ))}

        <div className="border-t mt-4 pt-4 text-xl font-bold">
          Total: ₹{total}
        </div>
      </div>
    </div>
  );
}
