import React from "react";

function Cart({ cartItems, removeFromCart }) {
  const total = cartItems.reduce((sum, item) => sum + item.price, 0);

  return (
    <div className="container mt-5">
      <h2 className="fw-bold mb-4">ðŸ›’ Your Cart</h2>

      {cartItems.length === 0 ? (
        <div className="text-center">
          <h4>Your cart is empty ðŸ˜¢</h4>
          <p>Add some beautiful gifts!</p>
        </div>
      ) : (
        <>
          {cartItems.map((item, index) => (
            <div
              key={index}
              className="row align-items-center border-bottom py-3"
            >
              <div className="col-md-2">
                <img
                  src={item.image}
                  alt={item.title}
                  className="img-fluid rounded"
                  style={{ height: "80px", objectFit: "cover" }}
                />
              </div>

              <div className="col-md-4">
                <h6 className="fw-bold">{item.title}</h6>
              </div>

              <div className="col-md-2 fw-bold text-danger">
                â‚¹{item.price}
              </div>

              <div className="col-md-2">
                <button
                  className="btn btn-outline-danger btn-sm"
                  onClick={() => removeFromCart(item.id)}
                >
                  Remove
                </button>
              </div>
            </div>
          ))}

          <div className="text-end mt-4">
            <h4>Total: <span className="text-success">â‚¹{total}</span></h4>
            <button className="btn btn-success mt-2">
              Proceed to Checkout

            </button>
          </div>
        </>
      )}
    </div>
    
  );
}

export default Cart;
