import React, { useState } from "react";
import { Link } from "react-router-dom";
import products from "../data/products";
import { addToCart } from "../utils/cart";

function Categories() {
  const [category, setCategory] = useState("All");
  const [quickView, setQuickView] = useState(null);

  const categories = ["All", "Birthday", "Anniversary", "Valentine", "Christmas"];

  const filteredProducts =
    category === "All"
      ? products
      : products.filter((p) => p.category === category);

  return (
    <div className="container-fluid mt-4">
      <div className="row">

        {/* SIDEBAR */}
        <div className="col-md-2 border-end px-4">
          <h5 className="fw-bold mb-3">Category</h5>
          {categories.map((cat) => (
            <div
              key={cat}
              className={`py-2 px-2 mb-2 rounded ${
                category === cat ? "bg-danger text-white" : ""
              }`}
              style={{ cursor: "pointer" }}
              onClick={() => setCategory(cat)}
            >
              {cat}
            </div>
          ))}
        </div>

        {/* PRODUCTS */}
        <div className="col-md-10">
          <div className="row g-4">
            {filteredProducts.map((product) => (
              <div className="col-md-3" key={product.id}>
                <div className="card h-100 shadow-sm border-0">

                  {/* PRODUCT IMAGE */}
                  <img
                    src={product.image}
                    alt={product.title}   // ✅ FIXED
                    className="card-img-top p-3"
                    style={{ height: "200px", objectFit: "contain" }}
                  />

                  <div className="card-body text-center">
                    <h6 className="fw-bold">{product.title}</h6>
                    <span className="fw-bold text-danger">₹{product.price}</span>

                    <div className="d-flex gap-2 mt-3">
                      <button
                        className="btn btn-outline-danger btn-sm w-50"
                        onClick={() => setQuickView(product)}
                      >
                        Quick View
                      </button>

                      <Link
                        to={`/product/${product.id}`}
                        className="btn btn-danger btn-sm w-50"
                      >
                        Details
                      </Link>
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>

      {/* QUICK VIEW MODAL */}
      {quickView && (
        <div
          className="modal fade show d-block"
          style={{ background: "rgba(0,0,0,0.6)" }}
        >
          <div className="modal-dialog modal-lg modal-dialog-centered">
            <div className="modal-content p-3">
              <h5>{quickView.title}</h5>

              {/* QUICK VIEW IMAGE */}
              <img
                src={quickView.image}
                alt={quickView.title}   // ✅ FIXED
                className="img-fluid mb-3"
              />

              <button
                className="btn btn-success"
                onClick={() => {
                  addToCart(quickView);
                  setQuickView(null);
                }}
              >
                Add to Cart
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default Categories;
