import { Link } from "react-router-dom";

export default function Home() {
  return (
    <>
      {/* HERO */}
      <div
        className="hero"
        style={{
          backgroundImage:
            "url(https://images.unsplash.com/photo-1549465220-1a8b9238cd48)"
        }}
      >
        <div className="hero-overlay">
          <div className="container text-center text-white">
            <h1 className="display-4 fw-bold">Make Every Moment Special üéÅ</h1>
            <p className="lead">Find perfect gifts for every occasion</p>
            <Link to="/categories" className="btn btn-danger btn-lg">
              Shop Now
            </Link>
          </div>
        </div>
      </div>

      {/* CATEGORIES */}
      <div className="container my-5">
        <h2 className="text-center mb-4 fw-bold">Shop by Occasion</h2>

        <div className="row">
          {["Birthday","Valentine","Anniversary","Christmas"].map(cat => (
            <div className="col-md-3 mb-4" key={cat}>
              <div className="card shadow-sm">
                <div className="image-box">
                  <img
                    src="https://images.unsplash.com/photo-1589302168068-964664d93dc0"
                    alt={cat}
                  />
                </div>
                <div className="card-body text-center">
                  <h5>{cat}</h5>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </>
  );
}
