import React, { useState } from "react";
import { Link } from "react-router-dom";
import products from "../data/products";

function Categories() {
  const categories = ["All", "Birthday", "Valentine", "Christmas", "Anniversary"];
  const [selected, setSelected] = useState("All");

  const filtered =
    selected === "All"
      ? products
      : products.filter((p) => p.category === selected);

  return (
    <div className="container mt-5">
      <h2 className="text-center mb-4">Gift Collection</h2>

      <div className="text-center mb-4">
        {categories.map((cat) => (
          <button
            key={cat}
            className={`btn me-2 ${
              selected === cat ? "btn-primary" : "btn-outline-primary"
            }`}
            onClick={() => setSelected(cat)}
          >
            {cat}
          </button>
        ))}
      </div>

      <div className="row">
        {filtered.map((p) => (
          <div className="col-md-3 mb-4" key={p.id}>
            <div className="card shadow-sm h-100">
              <img
                src={p.image}
                className="card-img-top"
                alt={p.title}
                style={{ height: "220px", objectFit: "cover" }}
              />
              <div className="card-body text-center">
                <h6>{p.title}</h6>
                <p className="fw-bold">â‚¹{p.price}</p>
                <Link to={`/product/${p.id}`} className="btn btn-danger btn-sm">
                  View Details
                </Link>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Categories;
