import React, { useState } from "react";
import { Link } from "react-router-dom";
import products from "../data/products";

function Categories({ addToCart }) {
  const [category, setCategory] = useState("All");
  const [selectedProduct, setSelectedProduct] = useState(null);

  const categories = [
    "All",
    "Birthday",
    "Anniversary",
    "Valentine",
    "Christmas",
  ];

  const filteredProducts =
    category === "All"
      ? products
      : products.filter((p) => p.category === category);

  return (
    <div className="container-fluid mt-4">
      <div className="row">

        {/* üîπ SIDEBAR */}
        <div className="col-md-2 bg-light p-4">
          <h5 className="fw-bold mb-3">üéÅ Categories</h5>

          {categories.map((cat) => (
            <button
              key={cat}
              className={`btn w-100 mb-2 text-start ${
                category === cat ? "btn-danger" : "btn-outline-danger"
              }`}
              onClick={() => setCategory(cat)}
            >
              {cat}
            </button>
          ))}
        </div>

        {/* üîπ PRODUCTS */}
        <div className="col-md-10">
          <h3 className="mb-4 fw-bold">{category} Gifts</h3>

          <div className="row">
            {filteredProducts.map((product) => (
              <div className="col-md-3 mb-4" key={product.id}>
                <div className="card border-0 shadow h-100">
                  <img
                    src={product.image}
                    alt={product.title}
                    className="card-img-top"
                    style={{ height: "220px", objectFit: "cover" }}
                  />

                  <div className="card-body text-center">
                    <h6 className="fw-bold">{product.title}</h6>
                    <p className="text-danger fw-bold">
                      ‚Çπ{product.price}
                    </p>

                    <button
                      className="btn btn-danger btn-sm me-2"
                      onClick={() => setSelectedProduct(product)}
                    >
                      Quick View
                    </button>

                    <Link
                      to={`/product/${product.id}`}
                      className="btn btn-outline-danger btn-sm"
                    >
                      View Page
                    </Link>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>

      {/* üîπ QUICK VIEW MODAL */}
      {selectedProduct && (
        <div
          className="modal fade show d-block"
          style={{ background: "rgba(0,0,0,0.6)" }}
        >
          <div className="modal-dialog modal-lg modal-dialog-centered">
            <div className="modal-content">

              <div className="modal-header">
                <h5 className="modal-title">
                  {selectedProduct.title}
                </h5>
                <button
                  className="btn-close"
                  onClick={() => setSelectedProduct(null)}
                ></button>
              </div>

              <div className="modal-body row">
                <div className="col-md-6">
                  <img
                    src={selectedProduct.image}
                    className="img-fluid rounded"
                    alt={selectedProduct.title}
                  />
                </div>

                <div className="col-md-6">
                  <h4 className="text-danger">
                    ‚Çπ{selectedProduct.price}
                  </h4>
                  <p>
                    üéÅ Perfect gift for special occasions like
                    birthdays & celebrations.
                  </p>

                  <button
                    className="btn btn-success me-2"
                    onClick={() => {
                      addToCart(selectedProduct);
                      setSelectedProduct(null);
                    }}
                  >
                    Add to Cart
                  </button>

                  <Link
                    to={`/product/${selectedProduct.id}`}
                    className="btn btn-outline-dark me-2"
                    onClick={() => setSelectedProduct(null)}
                  >
                    View Details
                  </Link>

                  <button
                    className="btn btn-secondary"
                    onClick={() => setSelectedProduct(null)}
                  >
                    Close
                  </button>
                </div>
              </div>

            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default Categories;
