import React from "react";
import { Link } from "react-router-dom";

function Home() {
  const categories = [
    {
      name: "Birthday",
      image:
        "https://images.unsplash.com/photo-1589302168068-964664d93dc0",
    },
    {
      name: "Valentine",
      image:
        "https://images.unsplash.com/photo-1529626455594-4ff0802cfb7e",
    },
    {
      name: "Christmas",
      image:
        "https://images.unsplash.com/photo-1543589077-47d81606c1bf",
    },
    {
      name: "Anniversary",
      image:
        "https://images.unsplash.com/photo-1519744792095-2f2205e87b6f",
    },
  ];

  return (
    <>
      {/* üî• HERO SECTION */}
      <div className="bg-danger text-white text-center p-5">
        <h1 className="fw-bold">Find the Perfect Gift üéÅ</h1>
        <p className="lead">
          Surprise your loved ones with beautiful gifts
        </p>
        <Link to="/categories" className="btn btn-light btn-lg mt-3">
          Shop Now
        </Link>
      </div>

      {/* üéØ CATEGORIES */}
      <div className="container mt-5">
        <h3 className="fw-bold text-center mb-4">
          Gift Categories
        </h3>

        <div className="row">
          {categories.map((cat) => (
            <div className="col-md-3 mb-4" key={cat.name}>
              <div className="card border-0 shadow h-100">
                <img
                  src={cat.image}
                  alt={cat.name}
                  className="card-img-top"
                  style={{
                    height: "200px",
                    objectFit: "cover",
                  }}
                />
                <div className="card-body text-center">
                  <h5 className="fw-bold">{cat.name}</h5>
                  <Link
                    to="/categories"
                    className="btn btn-outline-danger btn-sm"
                  >
                    View Gifts
                  </Link>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* üî• TRENDING GIFTS */}
      <div className="container mt-5 mb-5">
        <h3 className="fw-bold text-center mb-4">
          Trending Gifts üî•
        </h3>

        <div className="row">
          {[1, 2, 3, 4].map((item) => (
            <div className="col-md-3 mb-4" key={item}>
              <div className="card border-0 shadow h-100">
                <img
                  src="https://images.unsplash.com/photo-1509042239860-f550ce710b93"
                  alt="gift"
                  className="card-img-top"
                  style={{
                    height: "200px",
                    objectFit: "cover",
                  }}
                />
                <div className="card-body text-center">
                  <h6 className="fw-bold">Special Gift</h6>
                  <p className="text-danger fw-bold">‚Çπ499</p>
                  <Link
                    to="/categories"
                    className="btn btn-outline-danger btn-sm"
                  >
                    Buy Now
                  </Link>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </>
  );
}

export default Home;
