import React from "react";
import { useNavigate } from "react-router-dom";
import Navbar from "../components/Navbar";
import Footer from "../components/Footer";

function Home() {
  const navigate = useNavigate();

  return (
    <>
    <Navbar/>
    <Footer/>
      {/* HERO SECTION */}
      <div
        className="d-flex align-items-center text-white"
        style={{
          height: "80vh",
          backgroundImage:
            "url('https://images.unsplash.com/photo-1513201099705-a9746e1e201f')",
          backgroundSize: "cover",
          backgroundPosition: "center"
        }}
      >
        <div className="container text-center">
          <h1 className="fw-bold">Make Every Moment Special üéÅ</h1>
          <p className="fs-5">Beautiful gifts for every occasion</p>
          <button
            className="btn btn-warning btn-lg"
            onClick={() => navigate("/categories")}
          >
            Shop Now
          </button>
        </div>
      </div>

      {/* CATEGORY CARDS */}
      <div className="container mt-5">
        <h2 className="text-center mb-4">Shop by Occasion</h2>

        <div className="row">
          {[
            { name: "Birthday", img: "https://images.unsplash.com/photo-1578985545062-69928b1d9587" },
            { name: "Valentine", img: "https://images.unsplash.com/photo-1517841905240-472988babdf9" },
            { name: "Christmas", img: "https://images.unsplash.com/photo-1513885535751-8b9238bd345a" },
            { name: "Anniversary", img: "https://images.unsplash.com/photo-1526045612212-70caf35c14df" }
          ].map(cat => (
            <div className="col-md-3 mb-4" key={cat.name}>
              <div
                className="card shadow h-100"
                style={{ cursor: "pointer" }}
                onClick={() => navigate(`/categories?type=${cat.name}`)}
              >
                <img
                  src={cat.img}
                  className="card-img-top"
                  style={{ height: "220px", objectFit: "cover" }}
                  alt={cat.name}
                />
                <div className="card-body text-center">
                  <h5>{cat.name} Gifts</h5>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </>
  );
}

export default Home;
